        public static Sex AdaptChildGender(Gender gender)
        {
            switch(gender)
            {
                case Gender.MALE:
                    return Sex.Male;
                case Gender.FEMALE:
                    return Sex.Female;
                default:
                    return Sex.Male;
            }
        }

